<button type="button" onclick="editVendor"
        data-bs-target="#edit-vendor">
        Edit
</button>

    <!--primary theme Modal -->
    <div class="modal fade text-left" id="edit-vendor" tabindex="-1"
        role="dialog" aria-labelledby="myModalLabel160"
        aria-hidden="true">
        <div class="modal-dialog modal-dialog-centered modal-dialog-scrollable"
            role="document">
            <div class="modal-content">
                <div class="modal-header bg-primary">
                    <h5 class="modal-title white" id="myModalLabel160">
                        Primary Modal
                    </h5>
                    <button type="button" class="close"
                        data-bs-dismiss="modal" aria-label="Close">
                        <i data-feather="x"></i>
                    </button>
                </div>
                <div class="modal-body">
                    Tart lemon drops macaroon oat cake chocolate toffee
                    chocolate
                    bar icing. Pudding jelly beans
                    carrot cake pastry gummies cheesecake lollipop. I
                    love cookie
                    lollipop cake I love sweet
                    gummi
                    bears cupcake dessert.
                </div>
                <div class="modal-footer">
                    <button type="button"
                        class="btn btn-light-secondary"
                        data-bs-dismiss="modal">
                        <i class="bx bx-x d-block d-sm-none"></i>
                        <span class="d-none d-sm-block">Close</span>
                    </button>
                    <button type="button" class="btn btn-primary ml-1"
                        data-bs-dismiss="modal">
                        <i class="bx bx-check d-block d-sm-none"></i>
                        <span class="d-none d-sm-block">Accept</span>
                    </button>
                </div>
            </div>
        </div>
    </div>
